.oncology-section {
    background: linear-gradient(135deg, #38e4a5, #00bfa5);
    padding: 60px 10%;
    color: #fff;
    font-family: 'Arial', sans-serif;
	margin-bottom:70px;
}

/* Flexbox Layout */
.container {
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    justify-content: center;
    gap: 40px;
}

/* Text Content */
.text-content {
    flex: 1;
    min-width: 300px;
}

.title {
    font-size: 32px;
    font-weight: bold;
    margin-bottom: 15px;
    animation: fadeInUp 0.6s ease-in-out;
	font-weight: 600 !important;
}

.description {
    font-size: 18px;
    line-height: 1.6;
    animation: fadeInUp 0.8s ease-in-out;
}

/* Image Content */
.image-content {
    flex: 1;
    text-align: center;
    min-width: 300px;
}

.image-content img {
    width: 100%;
    max-width: 500px;
    border-radius: 12px;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);
    transition: transform 0.3s ease;
    animation: fadeInUp 1s ease-in-out;
}

.image-content img:hover {
    transform: scale(1.05);
}

/* Specialization Section */
.specialization {
    margin-top: 50px;
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    padding: 20px;
    border-radius: 12px;
    text-align: center;
    animation: fadeInUp 1.2s ease-in-out;
}

.specialization h4 {
    font-size: 22px;
    margin-bottom: 15px;
	font-weight: 600;
}

.specialization-list {
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    gap: 28px;
}

.card {
    background: rgba(255, 255, 255, 0.2);
    padding: 15px 20px;
    border-radius: 10px;
    font-size: 18px;
    color: white;
    display: flex;
    align-items: center;
    gap: 10px;
    transition: all 0.3s ease-in-out;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
}

.card svg {
    width: 30px;
    height: 30px;
    fill: none;
    stroke: white;
    stroke-width: 2;
}

.card:hover {
    background: rgba(255, 255, 255, 0.3);
    transform: translateY(-5px);
}

/* Responsive Design */
@media (max-width: 768px) {
    .container {
        flex-direction: column;
