			<script>
			document.getElementById('contactForm').addEventListener('submit', function(e) {
				e.preventDefault();
				
				const submitBtn = document.getElementById('submitBtn');
				const messageBox = document.getElementById('messageBox');
				
				// Disable submit button while processing
				submitBtn.disabled = true;
				submitBtn.textContent = 'Sending...';
				
				const formData = new FormData(this);

				fetch('/contact-submit', {
					method: 'POST',
					body: formData,
					headers: {
						'X-CSRF-TOKEN': document.querySelector('input[name="_token"]').value
					}
				})
				.then(response => response.json())
				.then(data => {
					messageBox.style.display = 'block';
					
					if (data.success) {
						messageBox.style.backgroundColor = '#dff0d8';
						messageBox.style.color = '#3c763d';
						messageBox.style.padding = '10px';
						messageBox.style.borderRadius = '4px';
						messageBox.textContent = 'Thank you for contacting us. We will get back to you soon.';
						
						// Clear form
						document.getElementById('contactForm').reset();
					} else {
						messageBox.style.backgroundColor = '#f2dede';
						messageBox.style.color = '#a94442';
						messageBox.style.padding = '10px';
						messageBox.style.borderRadius = '4px';
						messageBox.textContent = 'Something went wrong. Please try again.';
					}
				})
				.catch(error => {
					messageBox.style.display = 'block';
					messageBox.style.backgroundColor = '#f2dede';
					messageBox.style.color = '#a94442';
					messageBox.style.padding = '10px';
					messageBox.style.borderRadius = '4px';
					messageBox.textContent = 'Error submitting form. Please try again.';
				})
				.finally(() => {
					submitBtn.disabled = false;
					submitBtn.textContent = 'Submit';
				});
			});
			</script>

